// src/App.js
import React, { useState } from 'react';
import { processReceipt } from './api';
import './App.css';  // Import the CSS file here

function App() {
  const [file, setFile] = useState(null);
  const [output, setOutput] = useState(null);
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setOutput(null);
    setError(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      alert("Please upload an image.");
      return;
    }

    try {
      const response = await processReceipt(file);
      setOutput(response.data);
      setError(null);
    } catch (err) {
      console.error("Error processing file:", err);
      setError("There was an error processing the file. Please try again.");
    }
  };

  return (
    <div className="App">
      <h1>Receipt Reader</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit">Upload and Process</button>
      </form>

      {error && <div style={{ color: "red" }}><p>{error}</p></div>}

      {output && (
        <div>
          <h2>Processed Receipt Data:</h2>
          <pre>{JSON.stringify(output, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
