// src/App.js
import React, { useState } from 'react';
import { processReceipt } from './api';  // Import the API function

function App() {
  const [file, setFile] = useState(null); // For storing the selected file
  const [output, setOutput] = useState(null); // For storing the JSON output
  const [error, setError] = useState(null); // For displaying any errors

  // Handle file selection
  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setOutput(null); // Clear the previous output
    setError(null); // Clear previous errors
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      alert("Please upload an image.");
      return;
    }

    try {
      const response = await processReceipt(file);  // Call the API function
      setOutput(response.data);  // Store JSON response in output
      setError(null);
    } catch (err) {
      console.error("Error processing file:", err);
      setError("There was an error processing the file. Please try again.");
    }
  };

  return (
    <div className="App" style={{ textAlign: "center", padding: "20px" }}>
      <h1>Receipt Reader</h1>
      <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <button type="submit" style={{ marginLeft: "10px" }}>Upload and Process</button>
      </form>

      {/* Display error message if there's an error */}
      {error && (
        <div style={{ color: "red" }}>
          <p>{error}</p>
        </div>
      )}

      {/* Display JSON output if available */}
      {output && (
        <div>
          <h2>Processed Receipt Data:</h2>
          <pre style={{ textAlign: "left", backgroundColor: "#f4f4f4", padding: "10px", borderRadius: "5px" }}>
            {JSON.stringify(output, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}

export default App;
